mapper.update:
UPDATE course
SET is_active = "0"
WHERE course_ID = #{course_ID,jdbcType=VARCHAR}

UPDATE course
SET name = #{name,jdbcType=VARCHAR},
    description = #{description,jdbcType=LONGVARCHAR}
WHERE course_ID = #{course_ID,jdbcType=VARCHAR}

UPDATE course
SET attend_percentage = #{attend_percentage},
    project_percentage = #{project_percentage}
WHERE course_ID = #{course_ID,jdbcType=VARCHAR}
