mapper.update:
UPDATE report
SET report_name = #{report_name},
    score = #{score},
    submit_time = #{submit_time},
    correct_time = #{correct_time}
WHERE course_ID = #{course_ID}
  and project_name = #{project_name}
  and student_ID = #{student_ID}

update report
set score = #{score},correct_time = #{correct_time}
where course_ID = #{course_ID}
  and project_name = #{project_name}
  and student_ID = #{student_ID}

mapper.select:
select course_ID,project_name,student_ID,
       report_name,score,submit_time,correct_time
from report
where course_ID = #{course_ID}
  and student_ID = #{student_ID}
  and project_name = #{project_name}

select student_ID,name,report_name,submit_time,correct_time,score
from report natural join student
where course_ID = #{course_ID} and project_name = #{project_name}
