mapper.update:
UPDATE report
SET report_name = #{report_name},
    score = #{score},
    submit_time = #{submit_time},
    correct_time = #{correct_time}
WHERE course_ID = #{course_ID}
  and project_name = #{project_name}
  and student_ID = #{student_ID}

mapper.select:
select *
from report
where course_ID = #{course_ID}
  and project_name = #{project_name}
  and student_ID = #{student_ID}
